import { useState } from "react";

const BasicForm = props => {
  const [enteredFN, setEnteredFN] = useState("");
  const [fNIsValid, setFNIsValid] = useState(false);

  const fNHandler = event => {
    setEnteredFN(event.target.value);
    console.log(event.target.value);
  };

  if (enteredFN.trim() !== "") {
    setFNIsValid(true);
  }

  return (
    <form>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input type="text" id="name" onChange={fNHandler} />
          {fNIsValid && <p>First Name is required</p>}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
